//XML = javascript com html 
//Babel = interepretação do html+ css
//React = Componentizações reativas com xml
//Ciclo de vida = reatividade do react = montagem, atualização e desmontagem

function Form({name, setName, value, setValue}){
    return(
        <div>
            <input placeholder="nome" type="text" onChange={(evt)=>setName()} />
            <button>Filtrar</button>
            <input placeholder="valor" type="text"/>
            <button>Criar</button>
        </div>
    );
}

export default Form;
