//XML = javascript com html 
//Babel = interepretação do html+ css
//React = Componentizações reativas com xml
//Ciclo de vida = reatividade do react = montagem, atualização e desmontagem

function Form({name, setName, value, setValue, dataBase, setDataBase}){

    function handleClick(){
       const obj = {
           name,
           value,
       }
       setDataBase([...dataBase, obj])
       setName("")
       setValue("")
    }


    return(
        <div>
            <input placeholder="nome" type="text" value={name} onChange={(evt)=>setName(evt.target.value)}/>
            <button>Filtrar</button>
            <input placeholder="valor" type="text" value={value} onChange={(evt)=>setValue(evt.target.value)} />
            <button onClick={()=>handleClick()}>Criar</button>
        </div>
    );
}

export default Form;
